ACMDCLSRC.SOURCE:
@EndUserText.label: 'Mapping role for A_ChangeMasterCharcValue' 
@MappingRole: true 

define role A_ChangeMasterCharcValue { 
  grant select on A_ChangeMasterCharcValue 
    where  
      // Change Master authorization  
      ( ( _ChangeMaster.AuthorizationGroup ) ?= aspect pfcg_auth (  C_AENR_BGR, BEGRU, actvt = '03' ) and 
        ( ) = aspect pfcg_auth ( C_AENR_BGR, actvt = '03' ) ) and
      ( ( _ChangeMaster.AuthorizationGroup <> '' and  
        ( _ChangeMaster.AuthorizationGroup,
          _ChangeMaster.ChangeNumberStatus,
          _ChangeMaster.ChangeNumberReleaseKey,
          _ChangeMaster.ChangeNumberFunction  )  = aspect pfcg_auth( C_AENR_ERW, BEGRU, AENST, RLKEY, AEFUN, actvt = '03' ) ) or 
      ( ( _ChangeMaster.AuthorizationGroup = '' or 
          _ChangeMaster.AuthorizationGroup is null ) and 
        ( _ChangeMaster.ChangeNumberStatus,
          _ChangeMaster.ChangeNumberReleaseKey,
          _ChangeMaster.ChangeNumberFunction  ) = aspect pfcg_auth( C_AENR_ERW, AENST, RLKEY, AEFUN, actvt = '03' ) ) )

      // ClassType authorization
      and  ( ClassType ) = aspect pfcg_auth (  C_TCLA_BKA, KLART )
      // Characteristic authorization 
      and 
      (  
        ( CharcMaintAuthGrp ) ?= aspect pfcg_auth ( C_CABN_GRP, ATAUTH ) and
        ( ) =  aspect pfcg_auth (  C_CABN, ACTVT = '03' ) 
      )
      ; 
}
