ACMDCLSRC.SOURCE:
@EndUserText.label: 'Mapping role for A_DocumentInfoRecordCharc' 
@MappingRole: true 

define role A_Documentinforecordcharc { 
  grant select on A_DocumentInfoRecordCharc 
    where 
      ( _DocumentInfoRecord.AuthorizationGroup ) = aspect pfcg_auth ( C_DRAW_BGR, BEGRU ) and 
      ( DocumentInfoRecordDocType ) = aspect pfcg_auth ( C_DRAW_TCD, DOKAR, actvt = '03' ) and 
      ( DocumentInfoRecordDocType, _DocumentInfoRecord.InternalDocumentStatus ) = aspect pfcg_auth ( C_DRAW_TCS, DOKAR, DOKST, actvt = '03' )

    // ClassType authorization
      and ( ClassType ) = aspect pfcg_auth (  C_TCLA_BKA, KLART )
//     Class authorization      
      and 
      ( 
        ( 
          _ClassCharacteristics( P_KeyDate: $parameters.P_KeyDate )._ClassDetails( P_KeyDate : $parameters.P_KeyDate ).ClassMaintAuthGrp = '' and 
          ( ) =  aspect pfcg_auth (  C_KLAH_BKP, ACTVT = '03' ) 
        )  //display                                                           
        or 
        ( 
          _ClassCharacteristics( P_KeyDate: $parameters.P_KeyDate )._ClassDetails( P_KeyDate : $parameters.P_KeyDate ).ClassMaintAuthGrp <> '' and  
          ( _ClassCharacteristics( P_KeyDate: $parameters.P_KeyDate )._ClassDetails( P_KeyDate : $parameters.P_KeyDate ).ClassMaintAuthGrp ) =  aspect pfcg_auth (  C_KLAH_BKP, BGRKP, ACTVT = '03' ) 
        ) 
      ) //display      
    // Classification authorization
      and ( _ClassCharacteristics( P_KeyDate: $parameters.P_KeyDate )._ClassDetails( P_KeyDate: $parameters.P_KeyDate ).ClassClassfctnAuthGrp ) ?=  aspect pfcg_auth (  C_KLAH_BKL, BGRKL, actvt = '03' )
    // Characteristic authorization
      and 
      (  
        ( CharcMaintAuthGrp ) ?= aspect pfcg_auth ( C_CABN_GRP, ATAUTH ) and
        ( ) =  aspect pfcg_auth (  C_CABN, ACTVT = '03' ) 
      ) //display 
    ; 
}
