ACMDCLSRC.SOURCE:
@EndUserText.label: 'Mapping Role for ESH_S_CLFNCLASS'
@MappingRole: true
define role ESH_S_CLFNCLASS{
  grant select on ESH_S_CLFNCLASS
    // ClassType authorization    
      where ( ClassType ) =  aspect pfcg_auth ( C_TCLA_BKA, KLART )   
    // Class authorization
      and ( ( ClassMaintAuthGrp = '' and 
          ( ) = aspect pfcg_auth (  C_KLAH_BKP, ACTVT = '03' ) )  //display                                                           
        or 
          ( ClassMaintAuthGrp <> '' and  
          ( ClassMaintAuthGrp ) =  aspect pfcg_auth (  C_KLAH_BKP, BGRKP, ACTVT = '03' ) ) );//display
    // Characteristic authorization   
//      and ( 
//            ( CharcMaintAuthGrp = ''  and 
//            ( ) =  aspect pfcg_auth (  C_CABN, ACTVT = '03' ) //display
//          ) 
//      or    ( CharcMaintAuthGrp <> ''  and 
//            ( CharcMaintAuthGrp )  = aspect pfcg_auth ( C_CABN_GRP, ATAUTH ) and
//            ( ) =  aspect pfcg_auth (  C_CABN, ACTVT = '03' ) ) //display           
//  );                                                              
}
