@EndUserText.label: 'Mapping role for I_ClfnCharcValueDescForKeyDate'
@MappingRole: true
define role I_ClfnCharcValueDescForKeyDate {
  grant select on I_ClfnCharcValueDescForKeyDate
    // Characteristic authorization
      where ( 
            ( _CharacteristicValue( P_KeyDate : $parameters.P_KeyDate ).
                   _Characteristic( P_KeyDate : $parameters.P_KeyDate ).CharcMaintAuthGrp = ''  and 
            ( ) =  aspect pfcg_auth (  C_CABN, ACTVT = '03' ) //display
          )
      or    ( _CharacteristicValue( P_KeyDate : $parameters.P_KeyDate ).  
                   _Characteristic( P_KeyDate : $parameters.P_KeyDate ).CharcMaintAuthGrp <> ''  and 
            ( _CharacteristicValue( P_KeyDate : $parameters.P_KeyDate ).
                   _Characteristic( P_KeyDate : $parameters.P_KeyDate ).CharcMaintAuthGrp )  = aspect pfcg_auth ( C_CABN_GRP, ATAUTH ) and
            ( ) =  aspect pfcg_auth (  C_CABN, ACTVT = '03' ) ) //display 
          );
}
