@EndUserText.label: 'Mapping role for I_ClfnClassDescForKeyDate' 
@MappingRole: true 
define role I_ClfnClassDescForKeyDate { 
  grant select on I_ClfnClassDescForKeyDate
    // ClassType authorization
      where ( _Class( P_KeyDate : $parameters.P_KeyDate ).ClassType ) =  aspect pfcg_auth (  C_TCLA_BKA, KLART )
    // Class authorization
      and ( (  _Class( P_KeyDate : $parameters.P_KeyDate ).ClassMaintAuthGrp = '' and 
          ( ) =  aspect pfcg_auth (  C_KLAH_BKP, ACTVT = '03' ) )  //display                                                           
        or 
          ( _Class( P_KeyDate : $parameters.P_KeyDate ).ClassMaintAuthGrp <> '' and  
          ( _Class( P_KeyDate : $parameters.P_KeyDate ).ClassMaintAuthGrp ) =  aspect pfcg_auth (  C_KLAH_BKP, BGRKP, ACTVT = '03' ) )  //display   
  );  
}
