ACMDCLSRC.SOURCE:
@EndUserText.label: 'Mapping Role for I_ClfnObjectCharc'
@MappingRole: true
define role I_ClfnObjectCharc{
  grant select on I_ClfnObjectCharc
    // ClassType authorization    
      where ( ClassType ) =  aspect pfcg_auth ( C_TCLA_BKA, KLART )   
    // Class authorization
      and ( (  _Class.ClassMaintAuthGrp = '' and 
            ( ) =  aspect pfcg_auth (  C_KLAH_BKP, ACTVT = '03' ) )  //display                                                           
        or 
          ( _Class.ClassMaintAuthGrp <> '' and  
          ( _Class.ClassMaintAuthGrp ) =  aspect pfcg_auth (  C_KLAH_BKP, BGRKP, ACTVT = '03' ) ) ) //display    
    //Classification authorization   
      and ( ( _Class.ClassClassfctnAuthGrp = '' and 
          ( ) =  aspect pfcg_auth (  C_KLAH_BKL, ACTVT = '03' ) )  //display                                                           
        or 
          ( _Class.ClassClassfctnAuthGrp <> '' and  
          ( _Class.ClassClassfctnAuthGrp ) =  aspect pfcg_auth (  C_KLAH_BKL, BGRKL, ACTVT = '03' ) ) ) //display    
    //Characteristic authorization   
      and ( 
            ( _Characteristic.CharcMaintAuthGrp = ''  and 
            ( ) =  aspect pfcg_auth (  C_CABN, ACTVT = '03' ) //display
          ) 
        or (  _Characteristic.CharcMaintAuthGrp <> ''  and 
            ( _Characteristic.CharcMaintAuthGrp )  = aspect pfcg_auth ( C_CABN_GRP, ATAUTH ) and
            ( ) =  aspect pfcg_auth (  C_CABN, ACTVT = '03' ) ) //display 
          ); 
}
