@MappingRole: true
define role N_ClfnObjectCharcValForKeyDate{
  grant select on N_ClfnObjectCharcValForKeyDate
    //ClassType authorization
      where ( ClassType ) =  aspect pfcg_auth (  C_TCLA_BKA, KLART )  
    //Characteristic authorization      
      and ( 
            ( _Characteristic( P_KeyDate : $parameters.P_KeyDate ).CharcMaintAuthGrp = ''  and 
            ( ) =  aspect pfcg_auth (  C_CABN, ACTVT = '03' ) //display
          ) 
        or (  _Characteristic( P_KeyDate : $parameters.P_KeyDate ).CharcMaintAuthGrp <> ''  and 
            ( _Characteristic( P_KeyDate : $parameters.P_KeyDate ).CharcMaintAuthGrp )  = aspect pfcg_auth ( C_CABN_GRP, ATAUTH ) and
            ( ) =  aspect pfcg_auth (  C_CABN, ACTVT = '03' ) ) //display 
          );       
}
