@MappingRole: true
define role N_ClfnObjectClassForKeyDate{
  grant select on N_ClfnObjectClassForKeyDate
    // ClassType authorization    
      where ( ClassType ) =  aspect pfcg_auth ( C_TCLA_BKA, KLART )   
    // Class authorization
      and (  ( _Class.ClassMaintAuthGrp =  '' and                          ( ) = aspect pfcg_auth ( C_KLAH_BKP, ACTVT = '03' ) )          //display                                                           
        or   ( _Class.ClassMaintAuthGrp <> '' and ( _Class.ClassMaintAuthGrp ) = aspect pfcg_auth ( C_KLAH_BKP, BGRKP, ACTVT = '03' ) ) ) //display                  
    // Classification authorization
      and ( ( _Class.ClassClassfctnAuthGrp =  '' and                               ( ) =  aspect pfcg_auth (  C_KLAH_BKL, ACTVT = '03' ) )          //display                                                           
        or  ( _Class.ClassClassfctnAuthGrp <> '' and  ( _Class.ClassClassfctnAuthGrp ) =  aspect pfcg_auth (  C_KLAH_BKL, BGRKL, ACTVT = '03' ) ) ) //display     
    ;                                         
}
