define view I_ClfnObjectCharc 
  as select distinct from P_ClfnSingleObjectCharc as Single  
   
      association [1..1] to I_ClfnClass as _Class 
        on $projection.ClassInternalID = _Class.ClassInternalID 
        and $projection.ValidityStartDate <= _Class.ValidityEndDate
        and $projection.ValidityEndDate >= _Class.ValidityStartDate     
           
      /*+[hideWarning] { "IDS" : [ "CARDINALITY_CHECK" ] }*/  
      association [1..1] to I_ClfnCharacteristic as _Characteristic         
        on $projection.CharcInternalID = _Characteristic.CharcInternalID 
        // Usage of validity startr and end date replaces key field TimeIntervalNumber of characteristic in the condition        
        and $projection.ValidityStartDate <= _Characteristic.ValidityEndDate
        and $projection.ValidityEndDate >= _Characteristic.ValidityStartDate          
{
  key Single.ClfnObjectID,
  key Single.ClfnObjectTable,
  key Single.ClfnObjectType,
  key Single.ClassInternalID,
  key Single.CharcInternalID,
  key Single.TimeIntervalNumber,
      '000000000000000000' as ClfnObjectInternalID,
      Single.ClassType,
      Single.IsDeleted,
      Single.ChangeNumber,
      @Semantics.businessDate.from: true
      Single.ValidityStartDate,
      @Semantics.businessDate.to: true
      Single.ValidityEndDate,
      
      _Class,
      _Characteristic     
}
  
union 

  select distinct from P_ClfnMultipleObjectCharc as Multiple 
  
      association [1..1] to I_ClfnClass as _Class 
        on $projection.ClassInternalID = _Class.ClassInternalID 
        and $projection.ValidityStartDate <= _Class.ValidityEndDate
        and $projection.ValidityEndDate >= _Class.ValidityStartDate  
        
      /*+[hideWarning] { "IDS" : [ "CARDINALITY_CHECK" ] }*/
      association [1..1] to I_ClfnCharacteristic as _Characteristic         
        on $projection.CharcInternalID = _Characteristic.CharcInternalID 
        // Usage of validity startr and end date replaces key field TimeIntervalNumber of characteristic in the condition        
        and $projection.ValidityStartDate <= _Characteristic.ValidityEndDate
        and $projection.ValidityEndDate >= _Characteristic.ValidityStartDate          
{
  key Multiple.ClfnObjectID,
  key Multiple.ClfnObjectTable,
  key Multiple.ClfnObjectType,
  key Multiple.ClassInternalID,
  key Multiple.CharcInternalID,
  key Multiple.TimeIntervalNumber,
      Multiple.ClfnObjectInternalID,
      Multiple.ClassType,
      Multiple.IsDeleted,
      Multiple.ChangeNumber,
      @Semantics.businessDate.from: true
      Multiple.ValidityStartDate,
      @Semantics.businessDate.to: true
      Multiple.ValidityEndDate,
      
      _Class,
      _Characteristic   
}

