// Classification SDM Helper View for CAWN @ClientHandling.algorithm: #SESSION_VARIABLE @AbapCatalog: { sqlViewName: 'PSDMCAWN', compiler.compareFilter: true, preserveKey: true } @VDM: { private: true, viewType: #BASIC, lifecycle.contract.type: #NONE } @ObjectModel: { usageType.sizeCategory: #L, usageType.serviceQuality: #X, usageType.dataClass: #TRANSACTIONAL } @AccessControl.authorizationCheck: #NOT_ALLOWED
define view P_CLFNSDMCAWN as select distinct from cawn inner join cabn on cawn.atinn = cabn.atinn { key cawn.atinn, key cawn.atzhl, key cawn.adzhl, cawn.atflv, cawn.atflb, cawn.atcod, cawn.atstd, cawn.atawe, cawn.ataw1, cawn.atidn, cawn.spras, cawn.txtnr, cawn.datuv, cawn.techv, cawn.aennr, cawn.lkenz, cawn.dokar, cawn.doknr, cawn.dokvr, cawn.doktl, cawn.atzhh, cawn.knobj, cawn.atwhi, cawn.attlv, cawn.attlb, cawn.atprz, cawn.atinc, cawn.atvpl, cawn.datub,
cabn.atnam, cabn.atfor as chr_data_type, cabn.anzst as length, cabn.anzdz as decimals, cabn.atdim as exponent, cabn.atdex as exp_disp_format, cabn.msehi, cabn.atvor,
case when ( cawn.dec_from = 0 and // "is initial" clause cannot be used for fields of type D34N cawn.dec_to = 0 and // "is initial" clause cannot be used for fields of type D34N cawn.date_from is initial and cawn.date_to is initial and cawn.time_from is initial and cawn.time_to is initial and cawn.curr_from is initial and cawn.curr_to is initial and
// none initial source ( ( cawn.atflv is not initial or cawn.atflb is not initial ) or
// initial source, but internal value => boundary calculation // there are also 2 PPPI characeristics with initial ATCOD // => handle them as ATCOD = '1' ( cawn.atflv is initial and cawn.atflb is initial and ( cawn.atcod = '2' or cawn.atcod = '3' or cawn.atcod = '4' or cawn.atcod = '5' or ( cawn.atcod = '6' and cabn.atvor = 'X' ) or ( cawn.atcod = '6' and cabn.atvor <> 'X' and cabn.atdex = '2' ) or ( cawn.atcod = '7' and cabn.atvor = 'X' ) or cawn.atcod = '8' or cawn.atcod = '9' ) ) or
// initial source, but for currency the currency key still needs to be saved ( cabn.atfor = 'CURR' and cawn.currency is initial ) ) )
then cast( '01' as abap.char( 2 ) ) // SDM status: '01' - To be migrated else cast( '02' as abap.char( 2 ) ) // SDM status: '02' - Done end as sdm_status
} where cabn.atfor = 'NUM' or cabn.atfor = 'CURR' or cabn.atfor = 'DATE' or cabn.atfor = 'TIME' /*+[internal] { "BASEINFO": { "FROM": [ "CABN", "CAWN" ], "ASSOCIATED": [], "BASE": [], "ANNO_REF": [], "VERSION":0 } }*/
